CREATE TABLE `pub_content_list` ( `id` int(11) NOT NULL AUTO_INCREMENT, `context` text, `status` int(11), `pub_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=877 DEFAULT CHARSET=latin1; 
CREATE TABLE `weibo_content_detail` ( `id` int(11) NOT NULL AUTO_INCREMENT,`title` text, `context` text, `images` text, `link` text,`source` text, `md5` text, `status` int(11) DEFAULT 0, `pub_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf-8;
